  private byte[] t1(WordNumber[] data) {
    byte[] d1 = new byte[0x10000];
    for (int i = 0; i < d1.length; i++) {
      d1[i] = (byte) (data[i].intValue() & 0xff);
    }
    String s = Base64Utils.gzipArrayCompressToBase64(d1);
    s.toString();
    return d1;
  }
